const openButton=document.getElementById("open-sidebar-button"),navbar=document.getElementById("navbar"),media=window.matchMedia("(width < 768px)");media.addEventListener("change",e=>updateNavbar(e));function updateNavbar(e){const t=e.matches;t?navbar.setAttribute("inert",""):navbar.removeAttribute("inert")}function openSidebar(){const e=document.getElementById("overlay");navbar.classList.add("show"),e.classList.add("show"),openButton.setAttribute("aria-expanded","true")}function closeSidebar(){const e=document.getElementById("overlay");navbar.classList.remove("show"),e.classList.remove("show"),openButton.setAttribute("aria-expanded","false")}const navLinks=document.querySelectorAll("nav a");navLinks.forEach(e=>{e.addEventListener("click",()=>{closeSidebar()})}),document.addEventListener("click",e=>{!navbar.contains(e.target)&&!openButton.contains(e.target)&&closeSidebar()}),document.addEventListener("keydown",e=>{e.key==="Escape"&&closeSidebar()}),updateNavbar(media)