function initMobileMenu(){const e=document.querySelector(".burger-menu"),t=document.querySelector(".links"),s=document.body;if(!e||!t)return;function o(){e.classList.toggle("active"),t.classList.toggle("show"),s.classList.toggle("menu-open")}function n(){e.classList.remove("active"),t.classList.remove("show"),s.classList.remove("menu-open")}return e.addEventListener("click",o),document.addEventListener("click",s=>{!e.contains(s.target)&&!t.contains(s.target)&&n()}),document.addEventListener("keydown",e=>{e.key==="Escape"&&n()}),{closeMenu:n}}document.addEventListener("DOMContentLoaded",()=>{const e=initMobileMenu()})