const openButton=document.getElementById("open-sidebar-button"),navbar=document.getElementById("navbar"),media=window.matchMedia("(width < 768px)");media.addEventListener("change",e=>updateNavbar(e));function uodateNavbar(e){const t=e.matches;t?navbar.setAttribute("inert"):navbar.removeAttribute("inert")}function openSidebar(){navbar.classList.add("show"),openButton.setAttribute("aria-expanded","true"),navbar.removeAttribute("inert")}function closeSidebar(){navbar.classList.remove("show"),openButton.setAttribute("aria-expanded","false"),navbar.setAttribute("inert")}const navLinks=document.querySelectorAll("nav a");navLinks.forEach(e=>{e.addEventListener("click",()=>{closeSidebar()})}),updateNavbar(media)